contracts/ArcPaymentRole.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

interface IUSDCToken is IERC20 {
    function decimals() external view returns (uint8);
}

/**
 * @title ArcPaymentRole
 * @notice Multi-chain payroll system on Arc blockchain
 * Mirrors PayrollX Sui implementation with USDC on Arc
 * 
 * Features:
 * - Create payment roles with multi-recipient scheduled payments
 * - Fund roles with USDC
 * - Execute scheduled payments automatically
 * - Transparent developer fees
 * - Full audit trail on-chain
 */
contract ArcPaymentRole is Ownable, ReentrancyGuard {
    // ============ State Variables ============
    
    IERC20 public usdc;
    address public developer;
    uint256 public developerFeePercent = 1; // 1% fee
    
    // Role data structure
    struct PaymentSchedule {
        address recipient;
        uint256 amount; // in USDC (6 decimals)
        uint256 scheduledTime; // unix timestamp
        uint256 lastExecuted;
        bool active;
        string ensName; // Optional ENS name
    }
    
    struct Role {
        address sponsor;
        string name;
        uint256 startTime;
        uint256 expiryTime;
        uint256 totalFunded; // Total USDC received
        uint256 remainingBalance; // Current balance
        address leftoverRecipient; // Where unused funds go
        PaymentSchedule[] payments;
        bool active;
        uint256 createdAt;
    }
    
    // Role execution event tracking
    struct ExecutedPayment {
        address recipient;
        uint256 amount;
        uint256 timestamp;
        string ensName;
    }
    
    // Storage
    mapping(string => Role) public roles;
    mapping(string => bool) public roleExists;
    mapping(string => ExecutedPayment[]) public executionHistory;
    mapping(string => uint256[]) public fundingAmounts;
    mapping(string => address[]) public fundingDonors;
    
    uint256 public roleCounter = 0;
    
    // ============ Events ============
    
    event RoleCreated(
        string indexed roleId,
        address indexed sponsor,
        string name,
        uint256 timestamp
    );
    
    event RoleFunded(
        string indexed roleId,
        address indexed donor,
        uint256 amount,
        uint256 timestamp
    );
    
    event PaymentExecuted(
        string indexed roleId,
        address indexed recipient,
        uint256 amount,
        uint256 developerFee,
        uint256 timestamp
    );
    
    event RoleExpired(
        string indexed roleId,
        address indexed leftoverRecipient,
        uint256 leftoverAmount,
        uint256 timestamp
    );
    
    // ============ Constructor ============
    
    constructor(address _usdc) Ownable(msg.sender) {
        require(_usdc != address(0), "Invalid USDC address");
        usdc = IERC20(_usdc);
        developer = msg.sender;
    }
    
    // ============ Role Creation ============
    
    /**
     * @notice Create a new payment role
     * @param roleName Name of the role
     * @param startTime When payments can start executing (unix timestamp)
     * @param expiryTime When the role expires (unix timestamp)
     * @param recipients Array of recipient addresses
     * @param amounts Array of payment amounts (in USDC)
     * @param scheduledTimes Array of scheduled payment times (unix timestamps)
     * @param ensNames Array of ENS names (optional, can be empty strings)
     * @param leftoverRecipient Address to receive unused funds
     */
    function createRole(
        string memory roleName,
        uint256 startTime,
        uint256 expiryTime,
        address[] memory recipients,
        uint256[] memory amounts,
        uint256[] memory scheduledTimes,
        string[] memory ensNames,
        address leftoverRecipient
    ) external returns (string memory roleId) {
        require(bytes(roleName).length > 0, "Role name cannot be empty");
        require(startTime < expiryTime, "Start time must be before expiry");
        require(recipients.length == amounts.length, "Recipients and amounts length mismatch");
        require(recipients.length == scheduledTimes.length, "Recipients and times length mismatch");
        require(leftoverRecipient != address(0), "Invalid leftover recipient");
        
        // Generate unique role ID
        roleId = string(abi.encodePacked(
            abi.encodePacked(msg.sender),
            abi.encodePacked(block.timestamp),
            abi.encodePacked(roleCounter++)
        ));
        
        require(!roleExists[roleId], "Role ID already exists");
        roleExists[roleId] = true;
        
        // Create role
        Role storage role = roles[roleId];
        role.sponsor = msg.sender;
        role.name = roleName;
        role.startTime = startTime;
        role.expiryTime = expiryTime;
        role.remainingBalance = 0;
        role.leftoverRecipient = leftoverRecipient;
        role.active = true;
        role.createdAt = block.timestamp;
        
        // Add payment schedules
        for (uint256 i = 0; i < recipients.length; i++) {
            require(recipients[i] != address(0), "Invalid recipient");
            require(amounts[i] > 0, "Amount must be positive");
            
            role.payments.push(PaymentSchedule({
                recipient: recipients[i],
                amount: amounts[i],
                scheduledTime: scheduledTimes[i],
                lastExecuted: 0,
                active: true,
                ensName: i < ensNames.length ? ensNames[i] : ""
            }));
        }
        
        emit RoleCreated(roleId, msg.sender, roleName, block.timestamp);
    }
    
    // ============ Funding ============
    
    /**
     * @notice Fund a payment role with USDC
     * @param roleId ID of the role to fund
     * @param amount Amount of USDC to fund (in 6 decimals)
     */
    function fundRole(
        string memory roleId,
        uint256 amount
    ) external nonReentrant {
        require(roleExists[roleId], "Role does not exist");
        require(amount > 0, "Amount must be positive");
        
        Role storage role = roles[roleId];
        require(role.active, "Role is not active");
        require(block.timestamp < role.expiryTime, "Role has expired");
        
        // Transfer USDC from donor to contract
        require(
            usdc.transferFrom(msg.sender, address(this), amount),
            "USDC transfer failed"
        );
        
        // Update role balance
        role.totalFunded += amount;
        role.remainingBalance += amount;
        
        // Track funding
        fundingAmounts[roleId].push(amount);
        fundingDonors[roleId].push(msg.sender);
        
        emit RoleFunded(roleId, msg.sender, amount, block.timestamp);
    }
    
    // ============ Payment Execution ============
    
    /**
     * @notice Execute due payments for a role
     * @param roleId ID of the role
     */
    function executePayments(string memory roleId) external nonReentrant {
        require(roleExists[roleId], "Role does not exist");
        
        Role storage role = roles[roleId];
        require(role.active, "Role is not active");
        require(block.timestamp >= role.startTime, "Role has not started");
        require(role.remainingBalance > 0, "Insufficient balance");
        
        uint256 totalExecuted = 0;
        uint256 totalDevFee = 0;
        
        // Process each payment schedule
        for (uint256 i = 0; i < role.payments.length; i++) {
            PaymentSchedule storage schedule = role.payments[i];
            
            if (!schedule.active) continue;
            
            // Check if payment is due
            if (block.timestamp >= schedule.scheduledTime && schedule.lastExecuted == 0) {
                require(
                    role.remainingBalance >= schedule.amount,
                    "Insufficient balance for payment"
                );
                
                // Calculate developer fee (1% on execution)
                uint256 devFee = (schedule.amount * developerFeePercent) / 100;
                uint256 paymentAmount = schedule.amount - devFee;
                
                totalExecuted += schedule.amount;
                totalDevFee += devFee;
                
                // Transfer payment to recipient
                require(
                    usdc.transfer(schedule.recipient, paymentAmount),
                    "Payment transfer failed"
                );
                
                // Update schedule
                schedule.lastExecuted = block.timestamp;
                
                // Record execution
                executionHistory[roleId].push(ExecutedPayment({
                    recipient: schedule.recipient,
                    amount: paymentAmount,
                    timestamp: block.timestamp,
                    ensName: schedule.ensName
                }));
                
                emit PaymentExecuted(
                    roleId,
                    schedule.recipient,
                    paymentAmount,
                    devFee,
                    block.timestamp
                );
            }
        }
        
        // Update role balance
        if (totalExecuted > 0) {
            role.remainingBalance -= totalExecuted;
            
            // Transfer developer fee
            if (totalDevFee > 0) {
                require(
                    usdc.transfer(developer, totalDevFee),
                    "Developer fee transfer failed"
                );
            }
        }
        
        // Check if all payments executed or role expired
        bool allExecuted = true;
        for (uint256 i = 0; i < role.payments.length; i++) {
            if (role.payments[i].active && role.payments[i].lastExecuted == 0) {
                allExecuted = false;
                break;
            }
        }
        
        if (allExecuted || block.timestamp >= role.expiryTime) {
            role.active = false;
            
            // Send leftover funds
            if (role.remainingBalance > 0) {
                require(
                    usdc.transfer(role.leftoverRecipient, role.remainingBalance),
                    "Leftover transfer failed"
                );
                
                emit RoleExpired(
                    roleId,
                    role.leftoverRecipient,
                    role.remainingBalance,
                    block.timestamp
                );
                
                role.remainingBalance = 0;
            }
        }
    }
    
    // ============ View Functions ============
    
    /**
     * @notice Get complete role data
     */
    function getRole(string memory roleId)
        external
        view
        returns (
            address sponsor,
            string memory name,
            uint256 startTime,
            uint256 expiryTime,
            uint256 totalFunded,
            uint256 remainingBalance,
            address leftoverRecipient,
            bool active,
            uint256 paymentCount
        )
    {
        require(roleExists[roleId], "Role does not exist");
        Role storage role = roles[roleId];
        
        return (
            role.sponsor,
            role.name,
            role.startTime,
            role.expiryTime,
            role.totalFunded,
            role.remainingBalance,
            role.leftoverRecipient,
            role.active,
            role.payments.length
        );
    }
    
    /**
     * @notice Get payment schedules for a role
     */
    function getRolePayments(string memory roleId)
        external
        view
        returns (PaymentSchedule[] memory)
    {
        require(roleExists[roleId], "Role does not exist");
        return roles[roleId].payments;
    }
    
    /**
     * @notice Get execution history
     */
    function getExecutionHistory(string memory roleId)
        external
        view
        returns (ExecutedPayment[] memory)
    {
        return executionHistory[roleId];
    }
    
    /**
     * @notice Get funding donors
     */
    function getFundingHistory(string memory roleId)
        external
        view
        returns (address[] memory donors, uint256[] memory amounts)
    {
        donors = fundingDonors[roleId];
        amounts = fundingAmounts[roleId];
    }
    
    // ============ Admin Functions ============
    
    /**
     * @notice Update developer fee percentage
     */
    function setDeveloperFeePercent(uint256 feePercent) external onlyOwner {
        require(feePercent <= 10, "Fee too high (max 10%)");
        developerFeePercent = feePercent;
    }
    
    /**
     * @notice Update developer address
     */
    function setDeveloperAddress(address _developer) external onlyOwner {
        require(_developer != address(0), "Invalid address");
        developer = _developer;
    }
}

contracts/PaymentRole.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title PaymentRole - Chain-Abstracted Scheduled Payment System
 * @notice Automated USDC payouts on Arc, compatible with Sui version
 * @dev Part of multi-chain payment infrastructure (Arc + Sui)
 */
contract PaymentRole is Ownable, ReentrancyGuard {
    IERC20 public immutable usdc;
    
    struct Payment {
        address recipient;
        uint256 amount;
        uint256 scheduledTime;
        bool executed;
    }
    
    struct Role {
        address owner;
        string name;
        uint256 startTime;
        uint256 expiryTime;
        Payment[] payments;
        address leftoverRecipient;
        uint256 totalFunded;
        uint256 remainingBalance;
    }
    
    mapping(uint256 => Role) public roles;
    uint256 public roleCount;
    
    // Developer fee (1%)
    address public immutable developerAddress;
    uint256 public constant DEVELOPER_FEE_PERCENT = 100; // 1% = 100 basis points
    uint256 public constant BASIS_POINTS = 10000;
    
    // Events
    event RoleCreated(
        uint256 indexed roleId,
        string name,
        address indexed creator,
        uint256 startTime,
        uint256 expiryTime
    );
    
    event FundReceived(
        uint256 indexed roleId,
        address indexed sender,
        uint256 amount
    );
    
    event PaymentExecuted(
        uint256 indexed roleId,
        address indexed recipient,
        uint256 amount,
        uint256 timestamp
    );
    
    event PaymentDeadlineExtended(
        uint256 indexed roleId,
        uint256 paymentIndex,
        address indexed recipient,
        uint256 oldScheduledTime,
        uint256 newScheduledTime
    );
    
    event ExpiryExtended(
        uint256 indexed roleId,
        uint256 oldExpiry,
        uint256 newExpiry
    );
    
    event LeftoverTransferred(
        uint256 indexed roleId,
        address indexed recipient,
        uint256 amount
    );
    
    constructor(address _usdcAddress, address _developerAddress) Ownable(msg.sender) {
        require(_usdcAddress != address(0), "Invalid USDC address");
        require(_developerAddress != address(0), "Invalid developer address");
        usdc = IERC20(_usdcAddress);
        developerAddress = _developerAddress;
    }
    
    /**
     * @notice Create a new payment role with scheduled payments
     * @param name Role name
     * @param startTime When payments can start executing
     * @param expiryTime When role expires
     * @param recipients Array of recipient addresses
     * @param amounts Array of payment amounts (in USDC wei)
     * @param scheduledTimes Array of scheduled execution times
     * @param leftoverRecipient Address to receive leftover funds
     * @param developerFee Developer fee amount (1% of total)
     */
    function createRole(
        string memory name,
        uint256 startTime,
        uint256 expiryTime,
        address[] memory recipients,
        uint256[] memory amounts,
        uint256[] memory scheduledTimes,
        address leftoverRecipient,
        uint256 developerFee
    ) external nonReentrant returns (uint256) {
        require(startTime < expiryTime, "Invalid time range");
        require(recipients.length == amounts.length, "Length mismatch");
        require(amounts.length == scheduledTimes.length, "Length mismatch");
        require(leftoverRecipient != address(0), "Invalid leftover recipient");
        
        // Transfer developer fee
        require(
            usdc.transferFrom(msg.sender, developerAddress, developerFee),
            "Developer fee transfer failed"
        );
        
        uint256 roleId = roleCount++;
        Role storage role = roles[roleId];
        
        role.owner = msg.sender;
        role.name = name;
        role.startTime = startTime;
        role.expiryTime = expiryTime;
        role.leftoverRecipient = leftoverRecipient;
        role.totalFunded = 0;
        role.remainingBalance = 0;
        
        // Add payments
        for (uint256 i = 0; i < recipients.length; i++) {
            require(recipients[i] != address(0), "Invalid recipient");
            require(amounts[i] > 0, "Invalid amount");
            require(scheduledTimes[i] >= startTime && scheduledTimes[i] <= expiryTime, "Invalid schedule");
            
            role.payments.push(Payment({
                recipient: recipients[i],
                amount: amounts[i],
                scheduledTime: scheduledTimes[i],
                executed: false
            }));
        }
        
        emit RoleCreated(roleId, name, msg.sender, startTime, expiryTime);
        
        return roleId;
    }
    
    /**
     * @notice Fund a role with USDC
     * @param roleId Role ID to fund
     * @param amount Amount of USDC to deposit
     */
    function fundRole(uint256 roleId, uint256 amount) external nonReentrant {
        require(roleId < roleCount, "Invalid role ID");
        require(amount > 0, "Invalid amount");
        
        Role storage role = roles[roleId];
        require(block.timestamp < role.expiryTime, "Role expired");
        
        // Transfer USDC from sender
        require(
            usdc.transferFrom(msg.sender, address(this), amount),
            "USDC transfer failed"
        );
        
        role.totalFunded += amount;
        role.remainingBalance += amount;
        
        emit FundReceived(roleId, msg.sender, amount);
    }
    
    /**
     * @notice Execute ready payments for a role
     * @param roleId Role ID to execute payments for
     */
    function executePayments(uint256 roleId) external nonReentrant {
        require(roleId < roleCount, "Invalid role ID");
        
        Role storage role = roles[roleId];
        require(block.timestamp >= role.startTime, "Not started");
        
        for (uint256 i = 0; i < role.payments.length; i++) {
            Payment storage payment = role.payments[i];
            
            // Execute if ready and not already executed
            if (!payment.executed && block.timestamp >= payment.scheduledTime) {
                if (role.remainingBalance >= payment.amount) {
                    payment.executed = true;
                    role.remainingBalance -= payment.amount;
                    
                    require(
                        usdc.transfer(payment.recipient, payment.amount),
                        "Payment transfer failed"
                    );
                    
                    emit PaymentExecuted(roleId, payment.recipient, payment.amount, block.timestamp);
                }
            }
        }
    }
    
    /**
     * @notice Extend deadline for a specific payment (owner only)
     * @param roleId Role ID
     * @param paymentIndex Index of payment to extend
     * @param newScheduledTime New scheduled time
     */
    function extendPaymentDeadline(
        uint256 roleId,
        uint256 paymentIndex,
        uint256 newScheduledTime
    ) external {
        require(roleId < roleCount, "Invalid role ID");
        
        Role storage role = roles[roleId];
        require(msg.sender == role.owner, "Not owner");
        require(block.timestamp < role.expiryTime, "Role expired");
        require(paymentIndex < role.payments.length, "Invalid payment index");
        
        Payment storage payment = role.payments[paymentIndex];
        require(!payment.executed, "Already executed");
        require(newScheduledTime > payment.scheduledTime, "Must be later");
        require(newScheduledTime <= role.expiryTime, "Exceeds expiry");
        
        uint256 oldTime = payment.scheduledTime;
        payment.scheduledTime = newScheduledTime;
        
        emit PaymentDeadlineExtended(roleId, paymentIndex, payment.recipient, oldTime, newScheduledTime);
    }
    
    /**
     * @notice Extend role expiry time (owner only)
     * @param roleId Role ID
     * @param newExpiryTime New expiry time
     */
    function extendExpiry(uint256 roleId, uint256 newExpiryTime) external {
        require(roleId < roleCount, "Invalid role ID");
        
        Role storage role = roles[roleId];
        require(msg.sender == role.owner, "Not owner");
        require(block.timestamp < role.expiryTime, "Already expired");
        require(newExpiryTime > role.expiryTime, "Must be later");
        
        uint256 oldExpiry = role.expiryTime;
        role.expiryTime = newExpiryTime;
        
        emit ExpiryExtended(roleId, oldExpiry, newExpiryTime);
    }
    
    /**
     * @notice Execute expiry - pay remaining and send leftover
     * @param roleId Role ID
     */
    function executeExpiry(uint256 roleId) external nonReentrant {
        require(roleId < roleCount, "Invalid role ID");
        
        Role storage role = roles[roleId];
        require(block.timestamp > role.expiryTime, "Not expired");
        
        // Execute any remaining payments
        for (uint256 i = 0; i < role.payments.length; i++) {
            Payment storage payment = role.payments[i];
            
            if (!payment.executed && role.remainingBalance >= payment.amount) {
                payment.executed = true;
                role.remainingBalance -= payment.amount;
                
                require(
                    usdc.transfer(payment.recipient, payment.amount),
                    "Payment transfer failed"
                );
                
                emit PaymentExecuted(roleId, payment.recipient, payment.amount, block.timestamp);
            }
        }
        
        // Transfer leftover
        if (role.remainingBalance > 0) {
            uint256 leftover = role.remainingBalance;
            role.remainingBalance = 0;
            
            require(
                usdc.transfer(role.leftoverRecipient, leftover),
                "Leftover transfer failed"
            );
            
            emit LeftoverTransferred(roleId, role.leftoverRecipient, leftover);
        }
    }
    
    /**
     * @notice Get role details
     */
    function getRole(uint256 roleId) external view returns (
        address owner,
        string memory name,
        uint256 startTime,
        uint256 expiryTime,
        uint256 totalFunded,
        uint256 remainingBalance,
        uint256 paymentCount
    ) {
        require(roleId < roleCount, "Invalid role ID");
        Role storage role = roles[roleId];
        
        return (
            role.owner,
            role.name,
            role.startTime,
            role.expiryTime,
            role.totalFunded,
            role.remainingBalance,
            role.payments.length
        );
    }
    
    /**
     * @notice Get payment details
     */
    function getPayment(uint256 roleId, uint256 paymentIndex) external view returns (
        address recipient,
        uint256 amount,
        uint256 scheduledTime,
        bool executed
    ) {
        require(roleId < roleCount, "Invalid role ID");
        Role storage role = roles[roleId];
        require(paymentIndex < role.payments.length, "Invalid payment index");
        
        Payment storage payment = role.payments[paymentIndex];
        return (payment.recipient, payment.amount, payment.scheduledTime, payment.executed);
    }
    
    /**
     * @notice Get all payments for a role
     */
    function getAllPayments(uint256 roleId) external view returns (Payment[] memory) {
        require(roleId < roleCount, "Invalid role ID");
        return roles[roleId].payments;
    }
}

